import{_ as i,p as l,q as a,a1 as e}from"./framework-96b046e1.js";const h={},d=e('<h1 id="mplc-현대인의-생활문화" tabindex="-1"><a class="header-anchor" href="#mplc-현대인의-생활문화" aria-hidden="true">#</a> MPLC (현대인의 생활문화)</h1><h2 id="목차" tabindex="-1"><a class="header-anchor" href="#목차" aria-hidden="true">#</a> 목차</h2><h3 id="_1-글로벌-매너" tabindex="-1"><a class="header-anchor" href="#_1-글로벌-매너" aria-hidden="true">#</a> <a href="#%EA%B8%80%EB%A1%9C%EB%B2%8C-%EB%A7%A4%EB%84%88">1. 글로벌 매너</a></h3><h3 id="_2-소개와-인사1" tabindex="-1"><a class="header-anchor" href="#_2-소개와-인사1" aria-hidden="true">#</a> <a href="#%EC%86%8C%EA%B0%9C%EC%99%80-%EC%9D%B8%EC%82%AC-1">2. 소개와 인사1</a></h3><h3 id="_3-소개와-인사2" tabindex="-1"><a class="header-anchor" href="#_3-소개와-인사2" aria-hidden="true">#</a> <a href="#%EC%86%8C%EA%B0%9C%EC%99%80-%EC%9D%B8%EC%82%AC-2">3. 소개와 인사2</a></h3><h3 id="_4-세계-각-국의-인사법" tabindex="-1"><a class="header-anchor" href="#_4-세계-각-국의-인사법" aria-hidden="true">#</a> <a href="#%EC%84%B8%EA%B3%84-%EA%B0%81-%EA%B5%AD%EC%9D%98-%EC%9D%B8%EC%82%AC%EB%B2%95">4. 세계 각 국의 인사법</a></h3><h3 id="_5-명함매너" tabindex="-1"><a class="header-anchor" href="#_5-명함매너" aria-hidden="true">#</a> <a href="#%EB%AA%85%ED%95%A8-%EB%A7%A4%EB%84%88">5. 명함매너</a></h3><h3 id="_6-사교매너-방문-접대-임신-출산" tabindex="-1"><a class="header-anchor" href="#_6-사교매너-방문-접대-임신-출산" aria-hidden="true">#</a> <a href="#%EC%82%AC%EA%B5%90%EB%A7%A4%EB%84%88-%EB%B0%A9%EB%AC%B8-%EC%A0%91%EB%8C%80-%EC%9E%84%EC%8B%A0-%EC%B6%9C%EC%82%B0">6. 사교매너 방문 접대 임신 출산</a></h3><h3 id="_7-경조사-생일-결혼" tabindex="-1"><a class="header-anchor" href="#_7-경조사-생일-결혼" aria-hidden="true">#</a> <a href="#%EA%B2%BD%EC%A1%B0%EC%82%AC-%EC%83%9D%EC%9D%BC-%EA%B2%B0%ED%98%BC">7. 경조사 생일 결혼</a></h3><h3 id="_8-경조사-장례" tabindex="-1"><a class="header-anchor" href="#_8-경조사-장례" aria-hidden="true">#</a> <a href="#%EA%B2%BD%EC%A1%B0%EC%82%AC-%EC%9E%A5%EB%A1%80">8. 경조사 장례</a></h3><h3 id="_9-경조사-제례" tabindex="-1"><a class="header-anchor" href="#_9-경조사-제례" aria-hidden="true">#</a> <a href="#%EA%B2%BD%EC%A1%B0%EC%82%AC-%EC%A0%9C%EB%A1%80">9. 경조사 제례</a></h3><h3 id="_10-청탁금지법" tabindex="-1"><a class="header-anchor" href="#_10-청탁금지법" aria-hidden="true">#</a> <a href="#%EC%B2%AD%ED%83%81%EA%B8%88%EC%A7%80%EB%B2%95">10. 청탁금지법</a></h3><h3 id="_11-저작권법" tabindex="-1"><a class="header-anchor" href="#_11-저작권법" aria-hidden="true">#</a> <a href="#%EC%A0%80%EC%9E%91%EA%B6%8C%EB%B2%95">11. 저작권법</a></h3><h3 id="_12-직장매너" tabindex="-1"><a class="header-anchor" href="#_12-직장매너" aria-hidden="true">#</a> <a href="#%EC%A7%81%EC%9E%A5%EB%A7%A4%EB%84%88">12. 직장매너</a></h3><h3 id="_13-20대-자산형성" tabindex="-1"><a class="header-anchor" href="#_13-20대-자산형성" aria-hidden="true">#</a> <a href="#20%EB%8C%80-%EC%9E%90%EC%82%B0%ED%98%95%EC%84%B1">13. 20대 자산형성</a></h3><h3 id="_14-재무목표-설정-방법" tabindex="-1"><a class="header-anchor" href="#_14-재무목표-설정-방법" aria-hidden="true">#</a> <a href="#%EC%9E%AC%EB%AC%B4%EB%AA%A9%ED%91%9C-%EC%84%A4%EC%A0%95-%EB%B0%A9%EB%B2%95">14. 재무목표 설정 방법</a></h3><h3 id="_15-블랙-컨슈머" tabindex="-1"><a class="header-anchor" href="#_15-블랙-컨슈머" aria-hidden="true">#</a> <a href="#%EB%B8%94%EB%9E%99-%EC%BB%A8%EC%8A%88%EB%A8%B8">15. 블랙 컨슈머</a></h3><h3 id="_16-사회적-기업" tabindex="-1"><a class="header-anchor" href="#_16-사회적-기업" aria-hidden="true">#</a> <a href="#%EC%82%AC%ED%9A%8C%EC%A0%81-%EA%B8%B0%EC%97%85">16. 사회적 기업</a></h3><h3 id="_17-커뮤니케이션-매너" tabindex="-1"><a class="header-anchor" href="#_17-커뮤니케이션-매너" aria-hidden="true">#</a> <a href="#%EC%BB%A4%EB%AE%A4%EB%8B%88%EC%BC%80%EC%9D%B4%EC%85%98-%EB%A7%A4%EB%84%88">17. 커뮤니케이션 매너</a></h3><h3 id="_18-음주매너1" tabindex="-1"><a class="header-anchor" href="#_18-음주매너1" aria-hidden="true">#</a> <a href="#%EC%9D%8C%EC%A3%BC%EB%A7%A4%EB%84%881">18. 음주매너1</a></h3><h3 id="_19-음주매너2" tabindex="-1"><a class="header-anchor" href="#_19-음주매너2" aria-hidden="true">#</a> <a href="#%EC%9D%8C%EC%A3%BC%EB%A7%A4%EB%84%882">19. 음주매너2</a></h3><h3 id="_20-흡연매너" tabindex="-1"><a class="header-anchor" href="#_20-흡연매너" aria-hidden="true">#</a> <a href="#%ED%9D%A1%EC%97%B0%EB%A7%A4%EB%84%88">20. 흡연매너</a></h3><h3 id="_21-한국음식" tabindex="-1"><a class="header-anchor" href="#_21-한국음식" aria-hidden="true">#</a> <a href="#%ED%95%9C%EA%B5%AD-%EC%9D%8C%EC%8B%9D">21. 한국음식</a></h3><h3 id="_22-글로벌전통음식-브라질-중국" tabindex="-1"><a class="header-anchor" href="#_22-글로벌전통음식-브라질-중국" aria-hidden="true">#</a> <a href="#%EA%B8%80%EB%A1%9C%EB%B2%8C%EC%A0%84%ED%86%B5%EC%9D%8C%EC%8B%9D--%EB%B8%8C%EB%9D%BC%EC%A7%88-%EC%A4%91%EA%B5%AD">22. 글로벌전통음식 : 브라질, 중국</a></h3><h3 id="_23-글로벌전통음국-프랑스" tabindex="-1"><a class="header-anchor" href="#_23-글로벌전통음국-프랑스" aria-hidden="true">#</a> <a href="#%EA%B8%80%EB%A1%9C%EB%B2%8C-%EC%A0%84%ED%86%B5%EC%9D%8C%EC%8B%9D--%ED%94%84%EB%9E%91%EC%8A%A4">23. 글로벌전통음국 : 프랑스</a></h3><hr><h2 id="글로벌-매너" tabindex="-1"><a class="header-anchor" href="#글로벌-매너" aria-hidden="true">#</a> 글로벌 매너</h2><h3 id="매너" tabindex="-1"><a class="header-anchor" href="#매너" aria-hidden="true">#</a> 매너</h3><blockquote><p>인간관계를 맺는데 있어 중요한 요소</p></blockquote><ul><li><p>어원 : Manus(손, 행동, 습관) + Arius(방법, 방식)</p></li><li><p>매너의 기본(1)</p><blockquote><p>다른 사람의 인격을 존중하고, 배려하는 마음을 가지고, 편안하게 해주거나 기쁘게 만들려는 행동방식</p></blockquote></li><li><p>매너의 기본(2)</p><blockquote><p>역지사지 (易地思之)</p></blockquote></li><li><p>매너의 기본(3)</p><blockquote><p>형식에 치우치지 말고, 배려하는 마음을 전달하는 것</p></blockquote></li></ul><h3 id="매너와-에티켓의-차이" tabindex="-1"><a class="header-anchor" href="#매너와-에티켓의-차이" aria-hidden="true">#</a> 매너와 에티켓의 차이</h3><ul><li>매너(예의) : 상황에 따라 다르게 표현 가능</li><li>에티켓(범절) : 형식과 절차를 중요시 함</li></ul><h3 id="동양의-매너" tabindex="-1"><a class="header-anchor" href="#동양의-매너" aria-hidden="true">#</a> 동양의 매너</h3><ul><li><p>5덕</p><blockquote><p>인, 의, 예, 지, 신</p></blockquote></li><li><p>5륜</p><ul><li>군신유의 : 임금은 의로워야 하고 신하는 충성스러워야 한다</li><li>부자유친 : 부모는 자녀에게 인자하고 자녀는 부모를 공경해야한다.</li><li>부부유별 : 서로의 역할을 침법하지 않아야 한다</li><li>장유유서 : 어른과 어린아이 사이에는 질서가 있어야 한다</li><li>붕우유신 : 친구 사이에는 신뢰가 있어야한다</li></ul></li></ul><h3 id="서양의-매너-기원-성경" tabindex="-1"><a class="header-anchor" href="#서양의-매너-기원-성경" aria-hidden="true">#</a> 서양의 매너 (기원 : 성경)</h3><ul><li>자기를 높이는 사람은 낮아지고 자기를 낮추는 사람은 높아진다</li><li>네가 대접 받고자 하는 대로 다른 사람에게 행하라</li><li>네 이웃을 네 몸처럼 사랑하라</li></ul><h3 id="동서양-매너의-차이" tabindex="-1"><a class="header-anchor" href="#동서양-매너의-차이" aria-hidden="true">#</a> 동서양 매너의 차이</h3><ul><li>공통점 : 남을 존중하고 배려함</li><li>차이점 : 동양 -&gt; 남성우선, 서양 -&gt; 여성우선</li></ul><h2 id="소개와-인사-1" tabindex="-1"><a class="header-anchor" href="#소개와-인사-1" aria-hidden="true">#</a> 소개와 인사 1</h2><h3 id="자기소개" tabindex="-1"><a class="header-anchor" href="#자기소개" aria-hidden="true">#</a> 자기소개</h3><ul><li><p>성과 이름 (OOO 입니다)</p></li><li><p>성은 O가, 본관은 OO 입니다</p></li><li><p>아버지 함자는 홍 길자 동자 입니다</p></li><li><p>소개는 일어나서</p></li><li><p>손님과 주인의 인사 시 손님이 먼저 인사한다</p></li><li><p>직합-이름 순으로 자기소개 한다</p></li><li><h3 id="회사-직급" tabindex="-1"><a class="header-anchor" href="#회사-직급" aria-hidden="true">#</a> 회사 직급</h3><blockquote><p>사장 - 전무 - 이사 - 부장 - 차장 - 과장 - 대리 - 주임 - 사원</p></blockquote></li></ul><h3 id="소개-순서" tabindex="-1"><a class="header-anchor" href="#소개-순서" aria-hidden="true">#</a> 소개 순서</h3><ul><li>손아랫사람을 손윗사람에게</li><li>미혼자를 기혼자에게</li><li>한 사람을 그룹에게, 그룹에서 높은 사람 부터 (그룹 좌측 부터) 소개</li><li>애매한 경우 &quot;OO부터 소개드려도 될까요?&quot; 멘트</li><li>지위, 연령, 성별을 무시하고 자기 가족을 타인에게 먼저 소개</li></ul><h3 id="서신-소개" tabindex="-1"><a class="header-anchor" href="#서신-소개" aria-hidden="true">#</a> 서신 소개</h3><ul><li>소개 목적과 본인과의 관계를 밝힘</li><li>개봉한 상태에로 의뢰한 사람에게 전달 : 의뢰자는 내용을 확인 할 것</li><li>소개장을 써 주신 분에게 진행상황 말씀드릴 것</li></ul><h3 id="인사" tabindex="-1"><a class="header-anchor" href="#인사" aria-hidden="true">#</a> 인사</h3><blockquote><p>만났을 때나 헤어질 때 예를 표하는 말이나 행동</p></blockquote><h3 id="인사의-기능" tabindex="-1"><a class="header-anchor" href="#인사의-기능" aria-hidden="true">#</a> 인사의 기능</h3><ul><li>상대방에 대한 환영과 호의를 표시</li><li>상대방과의 관계를 설명해준다</li><li>처음 만나는 상대에 대한 경계심과 불안감 해소</li></ul><h3 id="공수" tabindex="-1"><a class="header-anchor" href="#공수" aria-hidden="true">#</a> 공수</h3><ul><li>두 손을 모아 앞으로 잡는 것</li><li>절의 기본 자세 손아랫 사람의 자세</li><li>남자는 왼손을 위, 여자는 오른손을 위 (흉사 시 반대로)</li></ul><h3 id="읍례-읍" tabindex="-1"><a class="header-anchor" href="#읍례-읍" aria-hidden="true">#</a> 읍례 (읍)</h3><ul><li>공수한 손을 올리는 것 (절 하기 전에)</li></ul><h2 id="소개와-인사-2" tabindex="-1"><a class="header-anchor" href="#소개와-인사-2" aria-hidden="true">#</a> 소개와 인사 2</h2><h3 id="우리나라-절의-종류" tabindex="-1"><a class="header-anchor" href="#우리나라-절의-종류" aria-hidden="true">#</a> 우리나라 절의 종류</h3><ul><li>큰절 : 부모님, 조부모님께, 의례행사시, 장례식때 영전에서</li><li>평절 : 장례식에서 상주와 하는 절</li><li>반절 : 웃어른이 아랫사람의 절에 대해 답배 할 때</li></ul><h3 id="여성의-큰절" tabindex="-1"><a class="header-anchor" href="#여성의-큰절" aria-hidden="true">#</a> 여성의 큰절</h3><ol><li>공수자세 (오른손이 위로)</li><li>공수한 손을 어깨 높이로 올린다. 고개를 숙여서 이마를 손등에 붙인다 (손의 높이 &lt; 어깨)</li><li>천천히 앉으면서 왼쪽 무릎을 먼저 꿇는다</li><li>오른발위에 왼발을 포갠다 (손이랑 반대)</li><li>엉덩이를 내려 깊게 앉는다</li><li>몸을 45도 앞으로 굽힌다 (손등을 이마에서 떼지 말것)</li><li>오른쪽 무릎부터 일어난다</li><li>절이 끝나면 손을 내린다</li><li>공수한 채 몸을 15도 굽혀 예를 표현 (굴신례)</li></ol><h3 id="여성의-평절" tabindex="-1"><a class="header-anchor" href="#여성의-평절" aria-hidden="true">#</a> 여성의 평절</h3><ol><li>공수자세 (오른손이 위로)</li><li>공수한 손을 풀어 양옆으로 내린다</li><li>천천히 앉으면서 왼쪽 무릎을 먼저 꿇는다</li><li>오른발위에 왼발을 포갠다</li><li>손끝이 바깥을 향하도록 하여 바닥에 댄다</li><li>허리를 30도 굽혔다가 허리를 펴며, 양손을 바닥에서 떼고 천천히 일어난다</li><li>오른쪽 무릎부터 일어난다</li><li>공수한 채 몸을 30도 굽혀 예를 표현 (굴신례)</li></ol><h3 id="남성의-큰절" tabindex="-1"><a class="header-anchor" href="#남성의-큰절" aria-hidden="true">#</a> 남성의 큰절</h3><ol><li>공수자세 (왼손이 위로)</li><li>읍례</li><li>공수한 손을 눈썹 높이로 올린다</li><li>허리와 무릎을 굽혀 공수한 손으로 바닥을 짚는다</li><li>왼쪽 무릎을 먼저 꿇는다</li><li>왼발이 앞에 되게 발등을 포갠다</li><li>팔꿈치는 열지 않고 몸통 쪽으로 붙이고 이마를 공수한 손에 댄다</li><li>오른쪽 무릎부터 일어난다</li><li>공수</li></ol><h3 id="남자의-평절" tabindex="-1"><a class="header-anchor" href="#남자의-평절" aria-hidden="true">#</a> 남자의 평절</h3><ol><li>모든 동작은 큰절과 동일하다</li><li>다만 읍례를 하지않고, 이마가 손등에 닿으면 즉시 일어난다</li></ol><h3 id="반평" tabindex="-1"><a class="header-anchor" href="#반평" aria-hidden="true">#</a> 반평</h3><ol><li>여성 : 평절 앉은 자세까지</li><li>남성 : 무릎을 꿇은 상태에서 머리와 엉덩이가 수평</li><li>이후에 공수자세 필요 X</li></ol><h3 id="경례" tabindex="-1"><a class="header-anchor" href="#경례" aria-hidden="true">#</a> 경례</h3><ul><li>3보 앞에서 멈춤, 눈을 마주침, 인사말 건넴, 경례, 다시 눈을 마주침</li><li>남자는 양쪽 손을 가볍게 주먹을 쥔 상태에서 바지 옆줄에 대고 경례</li><li>여자는 앞으로 모아 공수한 상태에서 경례</li></ul><h3 id="경례의-종류" tabindex="-1"><a class="header-anchor" href="#경례의-종류" aria-hidden="true">#</a> 경례의 종류</h3><ul><li>의례식 경례 (90도) : 관혼상제</li><li>큰경례 (45도) : 특별히 감사, 사과, 존경</li><li>평경례 (30도) : 평상 시</li><li>반경례 (15도) : 친한 사이</li></ul><h3 id="목례" tabindex="-1"><a class="header-anchor" href="#목례" aria-hidden="true">#</a> 목례</h3><ul><li>반복적인 만남</li><li>보행 중 일반 경례를 할 수 없을 때</li></ul><h3 id="악수" tabindex="-1"><a class="header-anchor" href="#악수" aria-hidden="true">#</a> 악수</h3><ul><li>손윗사람이 손아랫사람에게 청함</li><li>손아랫사람은 가벼운 목례를 하면서 악수</li><li>이성 간에는 여성이 먼저 악수를 청함 (목례, 미소로 대체 가능)</li></ul><h3 id="적절한-인사말" tabindex="-1"><a class="header-anchor" href="#적절한-인사말" aria-hidden="true">#</a> 적절한 인사말</h3><ul><li>손윗사람에게 수고하십시오 X</li></ul><h2 id="세계-각-국의-인사법" tabindex="-1"><a class="header-anchor" href="#세계-각-국의-인사법" aria-hidden="true">#</a> 세계 각 국의 인사법</h2><h3 id="인도" tabindex="-1"><a class="header-anchor" href="#인도" aria-hidden="true">#</a> 인도</h3><ul><li><p>나마스테 (나마 : 인사하니다, 아스 : 나, 테: 너)</p><ul><li><p>나는 당신한테 인사합니다</p><h3 id="중동" tabindex="-1"><a class="header-anchor" href="#중동" aria-hidden="true">#</a> 중동</h3></li></ul></li><li><p>앗사람 알라이쿰 (앗살람: 평화가, 알라이: ~위에, 쿰: 당신들)</p><h3 id="중국" tabindex="-1"><a class="header-anchor" href="#중국" aria-hidden="true">#</a> 중국</h3></li><li><p>손 흔들기 (나이에 상관없이)</p></li><li><p>공수 (손이 가슴위에 위치)</p><h3 id="태국" tabindex="-1"><a class="header-anchor" href="#태국" aria-hidden="true">#</a> 태국</h3></li><li><p>와이 (Wai) : 손을 합장한 모습이 Y와 비슷</p></li><li><p>손아랫사람이 고개를 깊이 숙이고, 손을 높이 든다</p></li><li><p>사와디캅: 안녕하세요</p></li></ul><h2 id="명함-매너" tabindex="-1"><a class="header-anchor" href="#명함-매너" aria-hidden="true">#</a> 명함 매너</h2><h3 id="명함의-기능" tabindex="-1"><a class="header-anchor" href="#명함의-기능" aria-hidden="true">#</a> 명함의 기능</h3><ul><li>자기를 소개하고 상대방으로 하여금 자기를 기억하게 함</li><li>인간관계 형성과 인맥관리의 중요한 도구</li><li>친분의 시작 : 첫 인상을 좌우, 예를 다해야 함</li></ul><h3 id="명함의-종류" tabindex="-1"><a class="header-anchor" href="#명함의-종류" aria-hidden="true">#</a> 명함의 종류</h3><ul><li>사교용 명함 (성명, 주소) <ul><li>메시지 카드나 초청장 대신 사용</li></ul></li><li>업무용 명함 (사진, 성명, 직장 주소, 로고 등) <ul><li>(미국) 사장, 중역용 : 명함 중앙에 이름, 하단에 직위와 회사명</li><li>(미국) 사원용 : 중앙에 회사명, 좌측 하단에 성명, 소속부서, 주소 등</li></ul></li></ul><h3 id="명함-교환-방법" tabindex="-1"><a class="header-anchor" href="#명함-교환-방법" aria-hidden="true">#</a> 명함 교환 방법</h3><ul><li><p>소개하고 서로 명함부터 교환</p></li><li><p>(서양) 헤어질 때 필요한 경우 교환</p></li></ul><ol><li><p>명함을 줄 때</p><ul><li>명함은 명함 지갑에 넣어서 저고리 안주머니에 보관</li><li>을 -&gt; 갑</li><li>선 자세에서 교환 한다.</li><li>테이블 위에 놓고 밀거나, 서류 봉투와 함께 X</li><li>일어서서, 정중하게 인사하고, 이름이나 소속을 밝히면서, 두 손으로 건넨다</li><li>명함 글자가 상대방 쪽에서 바르게 보이게 건넨다</li><li>상사가 먼저 건네고 자기 명함을 건넬 것</li><li>상대가 두 사람 이상이면 윗사람에게 먼저 건넨다</li><li>중간에 테이블 같은 장애물이 있다면 둘 사이에 장애물이 없는 상태에서 명함을 교환할 것</li></ul></li><li><p>명함을 받을 때</p><ul><li>일어서서, 두 손으로, 모서리를 잡고 (명함을 가리지 않고) 받는다</li><li>받으면 자신의 명함을 줄 것, 한 사람씩 주고 받는다</li><li>상대방의 명함을 놓고 가는 것은 실례</li><li>회의 할 때 자리에 따라 테이블에 명함을 올려 놓을 수 있음</li><li>상대방의 명함을 읽고 지갑에 넣을 것</li></ul></li><li><p>명함 보관</p><ul><li>명함 홀더나 바인더에 잘 정리 (직장별, 개인별, 모임별)</li><li>명함에 기재된 사항이 변경되는 경우 여백에 메모</li><li>연 1회 정리</li></ul></li></ol><h2 id="사교매너-방문-접대-임신-출산" tabindex="-1"><a class="header-anchor" href="#사교매너-방문-접대-임신-출산" aria-hidden="true">#</a> 사교매너 방문 접대 임신 출산</h2><h3 id="방문" tabindex="-1"><a class="header-anchor" href="#방문" aria-hidden="true">#</a> 방문</h3><h3 id="방문-시-기본-매너" tabindex="-1"><a class="header-anchor" href="#방문-시-기본-매너" aria-hidden="true">#</a> 방문 시 기본 매너</h3><ul><li><p>방문을 할경우 방문에 대한 약속이 선행</p></li><li><p>업무 관련 방문이 아니라면, 부부 동반</p></li><li><p>상대방의 취향 고려 선물 준비</p></li><li><p>방문 시간 : 오후 3시~5시 적당</p></li><li><p>병문안, 조문 제외하고는 오전 시간은 삼가</p><h3 id="가정으로의-방문" tabindex="-1"><a class="header-anchor" href="#가정으로의-방문" aria-hidden="true">#</a> 가정으로의 방문</h3></li><li><p>초인종을 누르기 전에 옷차림과 방문 목적을 점검</p></li><li><p>준비한 선물은 안주인에게 전달</p></li><li><p>거실에서는 입구 가까운 곳이 하석, 그 반대가 상석</p></li><li><p>응점실에 긴 소파와 안락의자가 있을 때 긴 소파가 손님용</p></li><li><p>식사시간은 피하되 부득이한 경우 너무 사양하지 않도록</p></li><li><p>부재 시는 명함을 남기도록</p></li><li><p>방문 후 반드시 전화로 감사를 표시</p><h3 id="직장으로의-방문" tabindex="-1"><a class="header-anchor" href="#직장으로의-방문" aria-hidden="true">#</a> 직장으로의 방문</h3></li><li><p>노크를 한 후 입실할 것</p></li><li><p>공용사무실인 경우 동료 직원들에게 간단한 목례</p></li><li><p>상대방의 일정을 파악</p><h3 id="병원으로의-방문" tabindex="-1"><a class="header-anchor" href="#병원으로의-방문" aria-hidden="true">#</a> 병원으로의 방문</h3></li><li><p>병문안은 빠를 수록 좋으며 방문전에 미리 알릴 것</p></li><li><p>문병 시간: 20~30분이 적당</p></li><li><p>식사시간과 치료시간을 피해 오후 2시 ~ 5시</p></li><li><p>환자가 원하는 것을 물어보아 준비</p></li><li><p>꽃은 삼가 (꽃가루)</p></li></ul><h3 id="접대" tabindex="-1"><a class="header-anchor" href="#접대" aria-hidden="true">#</a> 접대</h3><h3 id="접대매너" tabindex="-1"><a class="header-anchor" href="#접대매너" aria-hidden="true">#</a> 접대매너</h3><ul><li><p>응접실로 안내하며 자리를 권한 후 손님에게 가족들을 소개</p></li><li><p>손님이 소지품 두는 장소 마련</p></li><li><p>차는 손님 의향을 물어 대접</p></li><li><p>선물을 자리에서 열어보지 못한다면 소중히 취급</p></li><li><p>손님이 돌아갈 때 집에서 준비한 간단한 선물로 답례</p></li><li><p>현관이나 차를 타는 곳까지 배웅</p></li><li><p>손윗사람인 경우 나중에 잘 도착하셨는지 확인 전화 필수</p><h3 id="접대매너-직장" tabindex="-1"><a class="header-anchor" href="#접대매너-직장" aria-hidden="true">#</a> 접대매너 (직장)</h3></li><li><p>공항에 마중을 나가는 경우, 동등학 직급의 사람이 나가는 것이 좋음</p></li><li><p>상대방이 호텔 예약을 원한다면 호텔까지 안내</p></li><li><p>한번쯤 향토음식을 대접하거나 명승지를 소개하는 것도 좋음</p></li><li><p>손님이 갈때 무리하게 잡지 말고 간단한 인사말로 작별</p></li></ul><h3 id="임신-및-출산" tabindex="-1"><a class="header-anchor" href="#임신-및-출산" aria-hidden="true">#</a> 임신 및 출산</h3><h3 id="임신" tabindex="-1"><a class="header-anchor" href="#임신" aria-hidden="true">#</a> 임신</h3><ul><li><p>임신에 임하는 부모의 마음자세</p><blockquote><p>희생있는 사랑을 실천하겠다<br> 태어나는 새 생명을 인격적으로 대하겠다</p></blockquote><h3 id="출산" tabindex="-1"><a class="header-anchor" href="#출산" aria-hidden="true">#</a> 출산</h3></li><li><p>출산 : 병원에서 2-3일 입원</p></li><li><p>산후조리 : 산후조리원을 이용하기도 함</p></li><li><p>출산후 삼칠일 (21일) 동안 외부인의 방문을 자제</p></li><li><p>우리나라는 예로부터 아이를 낳으면 대문에 금줄을 쳤음</p><ul><li>아들이면 고추+숯+솔잎, 딸이면 숯+솔잎</li></ul></li></ul><h2 id="경조사-생일-결혼" tabindex="-1"><a class="header-anchor" href="#경조사-생일-결혼" aria-hidden="true">#</a> 경조사 생일 결혼</h2><h3 id="생일" tabindex="-1"><a class="header-anchor" href="#생일" aria-hidden="true">#</a> 생일</h3><ul><li>백일잔치 : 출생 백일 후 아이와 산모를 축하, 백설기를 나눠 먹음</li><li>첫돌 : 만 1년이 되는 첫 해 생일, 돌잡이</li><li>환갑 : 61세가 되는 생일, 일 갑자가 돌아옴 <ul><li>우리나라 나이로 60세 (만 59또는 58)</li></ul></li><li>연령별 생일 명칭 <ul><li>20세 (약관), 30세 (이립), 40세 (불혹), 50세 (지천명), 60세 (이순)</li><li>61세 (환갑), 62세 (진갑), 70세 (고희), 80세 (산수), 88세 (미수)</li><li>90세 (졸수), 100세 (상수)</li></ul></li></ul><h3 id="성년의-날" tabindex="-1"><a class="header-anchor" href="#성년의-날" aria-hidden="true">#</a> 성년의 날</h3><ul><li>만 19세가 되는 5월 셋째주 월요일</li><li>남자 (관례) : 댕기머리 자르고 상투를 만든 후 갓을 씌우는 의식</li><li>여자 (계례) : 머리 올려 비녀를 꽂는 의식</li><li>수수팥떡으로 액을 막음</li></ul><h3 id="혼례-결혼식" tabindex="-1"><a class="header-anchor" href="#혼례-결혼식" aria-hidden="true">#</a> 혼례 (결혼식)</h3><h3 id="전통혼례의-절차" tabindex="-1"><a class="header-anchor" href="#전통혼례의-절차" aria-hidden="true">#</a> 전통혼례의 절차</h3><ol><li><p>의혼 : 양가가 중매인을 통해 서로의 의사를 조정</p></li><li><p>앞놀이 : 신부집 마당에서 분위기를 북둗우는 공연</p></li><li><p>신랑입장 : 신랑이 기러기 아범과 함께 신부집으로 감</p></li><li><p>기러기를 전달 받은 신랑은 신부를 향해 예를 올리고, 기러기를 대청에 올려놓음</p></li><li><p>전안례 : 신부어머님께 기러기를 드리는 예식</p></li><li><p>초례청 : 신랑과 신부 입장</p></li><li><p>맞절 전 몸과 마음을 정갈하게 한다는 의미에서 손을 씻음</p></li><li><p>교배례 (맞절)</p><ul><li>신랑과 신부는 초례청에서 처음으로 인사를 나누며 서로에게 절</li><li>신랑과 신부는 백년해로를 서약</li></ul></li><li><p>합근례 : 술잔과 표주박에 각각 술을 부어 마시는 의식</p><h3 id="함-함진애비-납폐-봉채떡" tabindex="-1"><a class="header-anchor" href="#함-함진애비-납폐-봉채떡" aria-hidden="true">#</a> 함, 함진애비, 납폐, 봉채떡</h3></li></ol><ul><li><p>함 : 결혼 전날 신랑 집에서 신부 집으로 보내는 상자</p></li><li><p>함진애비 : 하인이나 사람을 사서 함을 지고 감</p></li><li><p>함 내용 : 오곡 주머니, 청홍채단, 혼서지 (혼인문서), 물목기, 예물 등</p></li><li><p>함진애비 일행은 신부 쪽 친지들과 여흥을 가짐</p></li><li><p>납폐 : 신랑 쪽에서 혼서와 채단, 예물을 함에 담아 보내는 것</p></li><li><p>봉채떡 : 함을 받기위해 신부 집에서 준비하는 음식</p><h3 id="현대식-결혼식" tabindex="-1"><a class="header-anchor" href="#현대식-결혼식" aria-hidden="true">#</a> 현대식 결혼식</h3></li><li><p>폐백 : 신부가 시댁 어른들께 첫인사 올리는 의례</p></li><li><p>순서 (부모 -&gt; 조부모 -&gt; 백부내외 -&gt; 숙부내외 -&gt; 형제)</p></li><li><p>들러리 : 형제 자매, 신랑 신부의 친구들, 친척, 화동</p></li></ul><h2 id="경조사-장례" tabindex="-1"><a class="header-anchor" href="#경조사-장례" aria-hidden="true">#</a> 경조사 장례</h2><h3 id="서양의-장례식" tabindex="-1"><a class="header-anchor" href="#서양의-장례식" aria-hidden="true">#</a> 서양의 장례식</h3><ul><li>교회에서 조용한 가운데 엄숙히 진행</li><li>미사나 예배가 끝난 후 공동묘지로 장례 행렬이 시작</li><li>행렬의 선두에는 목사님이나 신부님, 가장 가까운 유족, 친척과 지인 순으로 이동</li><li>남자 유족은 각자 세 삽씩 흙을 관위로 던지고 여자들은 꽃과 작은 꽃다발을 던짐</li></ul><h3 id="장례-절차" tabindex="-1"><a class="header-anchor" href="#장례-절차" aria-hidden="true">#</a> 장례 절차</h3><h3 id="장례-첫째날" tabindex="-1"><a class="header-anchor" href="#장례-첫째날" aria-hidden="true">#</a> 장례 첫째날</h3><ol><li><p>임종(운명) : 마지막 숨이 넘어가는 것</p></li><li><p>수시 : 눈을 감기고 팔다리를 가지런히 하여 코와 입을 솜으로 막아준다</p></li><li><p>사망증명서 : 의사 또는 시, 군 검사관이 사망한지 24시간 이내에 발급한다</p></li><li><p>발상 : 초상이 났음을 외부에 알리고 상례를 시작함을 말한다</p></li><li><p>부고 : 고인과 유족의 가까운 친인척, 지인, 회사 등에 부고를 낸다</p><h3 id="장례-둘째날" tabindex="-1"><a class="header-anchor" href="#장례-둘째날" aria-hidden="true">#</a> 장례 둘째날</h3></li><li><p>염습 : 소독된 솜이나 거즈로 시신을 씻기며(염), 수의를 입힌다(습)</p></li><li><p>입관 : 시신을 관에 모신다</p></li><li><p>영좌 설치 : 영정사진을 제상 위에 모시고 영정 양 옆으로 촛불을 밝힌다</p></li><li><p>성복 : 상제는 상복을 입는다</p></li><li><p>조문 : 성복이 끝나면 조문을 받는다</p><ul><li><p>조문 : 돌아가신 망자의 복을 빌고, 슬픔을 위로하기 위해 장례식장에 가는 행위</p><h3 id="장례-셋째날" tabindex="-1"><a class="header-anchor" href="#장례-셋째날" aria-hidden="true">#</a> 장례 셋째날</h3></li></ul></li><li><p>발인 : 영구가 집을 떠나는 절차이며 발인제를 지낸다</p></li><li><p>운구 : 영구를 장지(화장지)까지 장의차나 상여로 운반</p></li><li><p>하관 : 장지에서 영구(관)을 광중(구덩이)에 넣는다</p></li><li><p>성분(봉분) : 관을 완전히 덮은 후, 흙을 쌓아 올려 잔디를 입힌다</p></li><li><p>위령제(성분제) : 묘소 앞에 영좌를 모시고 제사를 올린다</p><h3 id="장례-후-제의" tabindex="-1"><a class="header-anchor" href="#장례-후-제의" aria-hidden="true">#</a> 장례 후 제의</h3></li><li><p>반우제 : 집으로 돌아와 영혼을 집에 맞아들이는 제사</p></li><li><p>삼우제 : 묘소에서 장례를 치른지 3일째 되는 날에 드리는 제사</p></li><li><p>사십구제 : 장례일로부터 49일째 되는 날에 지내는 제사</p></li><li><p>백일제 : 장례 후 백일째 되는 날에 지내는 제사</p><h3 id="조문-예절" tabindex="-1"><a class="header-anchor" href="#조문-예절" aria-hidden="true">#</a> 조문 예절</h3></li></ol><ul><li><p>방명록 서명 -&gt; 분향과 헌화 -&gt; 재배(큰절2) -&gt; 조문(평절1) -&gt; 부의금 전달</p></li><li><p>가까운 사람이라면 즉시 찾아가서 애도의 뜻을 전하고 장례 절차를 도와줌</p></li><li><p>일반적으로 염이 끝나고 준비가 된 임종일 다음날 조문을 간다</p><h3 id="문상-시-복장" tabindex="-1"><a class="header-anchor" href="#문상-시-복장" aria-hidden="true">#</a> 문상 시 복장</h3></li><li><p>검은색, 단정하게, 화려한 액세서리X, 진한 메이크업X</p><h3 id="조문-시-인사말" tabindex="-1"><a class="header-anchor" href="#조문-시-인사말" aria-hidden="true">#</a> 조문 시 인사말</h3><blockquote><p>&quot;얼마나 상심이 크신지요&quot;, &quot;위로의 말씀을 올립니다&quot;</p></blockquote><h3 id="조문시-영전에-분향하는-방법" tabindex="-1"><a class="header-anchor" href="#조문시-영전에-분향하는-방법" aria-hidden="true">#</a> 조문시 영전에 분향하는 방법</h3></li><li><p>유교식 : 영전에 분향 후 큰절을 두 번하고 난 후, 상주와 평절</p></li><li><p>기독교식 : 꽃을 올리고 (꽃봉오리가 조문객 쪽으로), 기도(묵념)을 한 후 상주와 평절</p><h3 id="조의금-전달" tabindex="-1"><a class="header-anchor" href="#조의금-전달" aria-hidden="true">#</a> 조의금 전달</h3></li><li><p>문상을 마친 후 물러나와 부의함에 직접 넣음</p></li><li><p>상주에게 직접 건네는 것을 결례</p></li><li><p>봉투에 부의, 근조, 조의 등이라 씀</p></li></ul><h2 id="경조사-제례" tabindex="-1"><a class="header-anchor" href="#경조사-제례" aria-hidden="true">#</a> 경조사 제례</h2><h3 id="제례의-종류" tabindex="-1"><a class="header-anchor" href="#제례의-종류" aria-hidden="true">#</a> 제례의 종류</h3><ol><li>기제사 <ul><li>돌아가신 부모님과 조부모님을 기리는 제사</li><li>의미 : 돌아가신 부모, 조부모님을 생각하며 뿌리의식과 정체성 찾기</li><li>시간 : 돌아가신 날의 자시 (밤 11시 ~ 새벽 1시)에 시작</li><li>기독교, 천주교도인 : 가족 예배, 추도식으로 대신함</li></ul></li><li>차례 <ul><li>명절날 아침에 지냄</li><li>잔을 한 번 올리고, 축문을 읽지 않음</li><li>촛불을 키지 않음</li></ul></li><li>시제사 (시향) <ul><li>기제를 모시지 않은 조상을 모시는 제사</li><li>음력 10월에 실시</li><li>직계자손, 방계자손은 누구나 참여</li><li>낮에 야외에서 지내며 기제절차와 같음</li></ul></li></ol><h3 id="차례상" tabindex="-1"><a class="header-anchor" href="#차례상" aria-hidden="true">#</a> 차례상</h3><ul><li><p>바라봤을 때 오른쪽 동쪽, 왼쪽 서쪽</p></li><li><p>1열 (수저, 잔, 떡국, 시루떡)</p></li><li><p>2열 (전, 육적, 소적, 전, 어적)</p></li><li><p>3열 (육탕, 소탕, 어탕)</p></li><li><p>4열 (포, 나물, 간장, 나박김치, 식혜)</p></li><li><p>5열 (과일, 한과)</p></li><li><p>좌포우혜 : 좌측에 포, 우측에 식혜</p></li><li><p>어동육서 : 동쪽에 어류, 서쪽에 육류</p></li><li><p>두동미서 : 생선의 머리는 동쪽 꼬리는 서쪽</p></li><li><p>조율이시 : 대추, 밤, 배, 감</p></li></ul><h3 id="제사음식과-기본-상식" tabindex="-1"><a class="header-anchor" href="#제사음식과-기본-상식" aria-hidden="true">#</a> 제사음식과 기본 상식</h3><ul><li>복숭아, &#39;치&#39;자 생선 (꽁치, 참치, 갈치)는 사용하지 않는다</li><li>고춧가루, 마늘 양념을 사용하지 않는다</li><li>진설의 순서는 시접(수저 담아 놓는 그릇)과 잔반을 먼저 올린뒤에 1열 부터 놓는다</li><li>설에는 떡국, 추석에는 송편, 기제사는 메(밥)을 올린다</li><li>식혜, 탕, 면은 건더기를 사용한다</li></ul><h3 id="신주-신위" tabindex="-1"><a class="header-anchor" href="#신주-신위" aria-hidden="true">#</a> 신주 (신위)</h3><ul><li>현 (나타날 현)</li><li>관계 (아버지 : 고, 어머니 : 비, 할아버지 : 조고, 할머니 : 조비)</li><li>이름</li><li>신위</li></ul><h2 id="청탁금지법" tabindex="-1"><a class="header-anchor" href="#청탁금지법" aria-hidden="true">#</a> 청탁금지법</h2><h3 id="청탁금지법-김영란법" tabindex="-1"><a class="header-anchor" href="#청탁금지법-김영란법" aria-hidden="true">#</a> 청탁금지법 (김영란법)</h3><ul><li>우리나라 공직자 등에게 부정청탁 및 금품수수를 금지함</li><li>적용 : 2년 이하 징역 또는 2000만원 이하 과태료</li></ul><h3 id="_1-부정청탁-금지" tabindex="-1"><a class="header-anchor" href="#_1-부정청탁-금지" aria-hidden="true">#</a> 1. 부정청탁 금지</h3><ul><li>청탁대상 <ul><li>국가/지방, 공직 유관단체, 공공기관과 공무원과 배우자</li><li>국/공/사림 학교 교직원, 학교법인의 임직원, 배우자</li><li>언론사 종사자와 배우자</li></ul></li><li>예외 조항 :<strong>공익적인 목적</strong>으로 고충 민원을 전달하는 행위 등</li><li>신고 방법 <ul><li>받은 금품을 돌려주고 거부 의사를 표현</li><li>조치 이후에도 다시 받는다면 소속 기관장에게 서면(전자문서 포함)으로 신고</li></ul></li></ul><h3 id="_2-금품수수-금지" tabindex="-1"><a class="header-anchor" href="#_2-금품수수-금지" aria-hidden="true">#</a> 2. 금품수수 금지</h3><ul><li>대상 <ul><li>동일인으로부터 1회 100만원초과 매 회계연도 300만원 초과 금품 등 수수</li><li>직무와 관련하여 1회 100만원 이하 금품 등 수수</li><li>배우자가 수수 금지 금품 등을 받은 경우</li><li>외부 강의시 대통령령으로 정하는 금액을 초과하는 사례금 수수</li><li>식사비 (3만원), 선물 (5만원), 경조사비 (5만원) (결혼, 장례만), 금품 (100만원)</li></ul></li><li>처벌 <ul><li>과태료 : 금품수수 (수수금액 2배 이상 5배이하), 강의 사례금(500만원 이하)</li><li>형사 처벌 : 동일인으로부터 ... (3년이하 징역 3천만원이하 벌금)</li></ul></li></ul><h3 id="연습문제" tabindex="-1"><a class="header-anchor" href="#연습문제" aria-hidden="true">#</a> 연습문제</h3><ul><li>공직자 자녀 돌잔치에 참여하며 축하금으로 5만원을 제공하는 것 -&gt; 위반 (돌잔치는 경조사비 X)</li><li>공직자가 결혼식 하객으로 참석하여 3만원 이상의 식사를 제공받은 행위 -&gt; 위반X (모든 사람들에게 대접했으므로)</li><li>공직자 부친의 장례식에 부의금 5만원과 별도의 화환을 보내는 경우 -&gt; 부의금 + 화환 10만원 이하일 경우 위반 X</li></ul><h2 id="저작권법" tabindex="-1"><a class="header-anchor" href="#저작권법" aria-hidden="true">#</a> 저작권법</h2><h3 id="저작권" tabindex="-1"><a class="header-anchor" href="#저작권" aria-hidden="true">#</a> 저작권</h3><blockquote><p>저작권 : 창작자가 가지는 권리<br> 저작물 : 사람의 사상이나 감정을 일정한 형식에 담아, 이를 다른 사람들이 느끼고 깨달을 수 있도록 표현한 것.</p></blockquote><ul><li><p>저작권법의 목적</p><ul><li>저작자의 권리를 보호하고 저작물의 공정한 이용을 도모함으로써 <strong>문화산업을 발전</strong>시키는데 있다.</li></ul></li><li><p>저작권의 표시</p><blockquote><p>Copyright ©2022 Apple Inc.</p></blockquote></li><li><p>저작권법 제정 배경 15세기 출판인쇄술의 발명으로 문서의 대량복제가 가능해짐</p></li><li><p>우리나라의 저작권법</p><ul><li>1957년 저작권법 제정 (일본 저작권법 제정업</li><li>1987년 국제시대의 비난으로 저작권법 개정</li><li>1996년 베른 협약 가입</li></ul></li><li><p>무행동 방식 주의 (우리나라의 저작권법)</p><ul><li>=&gt; 저작물을 창작하는 동시에 자동으로 발생하는 것으로 봄</li><li>=&gt; 저작권의 발생은 어떠한 절차나 행동방식을 요하지 않는다는 것을 의미</li><li>조건 : 자신만의 표현 방식으로 독자적으로 표현할 것</li></ul></li><li><p>방식주의 (우리나라의 특허권)</p><ul><li>=&gt; 기술 공개의 대가로 독점권을 부여함으로써 발명의욕을 고취함</li><li>조건 : 이미 알려진 기술이 아니어야하고 산업에 이용 가능 한 기술일 것</li></ul></li><li><p>법적 보호를 받는 저작물이 되는 요건</p><ol><li>독창성이 있어야 한다</li><li>다른 사람이 느껴서 알 수 있을 정도로 외부에 표현되어야 한다</li></ol></li><li><p>저작 인격권</p><ul><li>저작자의 명예와 인격적 이익을 보호하기 위한 권리</li><li>공표권, 성명표시권, 동일성 유지권 (단편소설 -&gt; 만화 제작하는 경우)</li></ul></li><li><p>저작재산권</p><ul><li>=&gt; 저작물을 어떤 방법으로 이용하느냐에 따른 권리</li><li>복제권 : 복사, 인쇄, 사진, 녹음, 녹화 등의 방법으로 다시 제작하는 것</li><li>공연권, 공중송신권, 전시권, 배포권, 대여권, 2차적 저작물 작성권</li></ul></li><li><p>저작인접권</p><ul><li>저작물의 전달자로서의 역할을 하는 자에게 부여되는 권리</li></ul></li></ul><h2 id="직장매너" tabindex="-1"><a class="header-anchor" href="#직장매너" aria-hidden="true">#</a> 직장매너</h2><h3 id="호칭" tabindex="-1"><a class="header-anchor" href="#호칭" aria-hidden="true">#</a> 호칭</h3><ul><li>상급자 : 성씨+직위+님, 직위+님</li><li>동급자 <ul><li>성씨+직위, 성씨+직위+님 (안 친하거나 나이가 많을 때)</li><li>성명+씨 (연하이거나 직급이 없을 때)</li></ul></li><li>하급자 : 성명+씨, 선배님(연장자)</li><li>직장 상사의 가족 : 남편(성+선생님), 부인(사모님), 자녀(성명+씨)</li><li>동료의 가족 : 남편 (부군, 바깥선생님), 아내(부인, 안주인)</li></ul><h3 id="인사말" tabindex="-1"><a class="header-anchor" href="#인사말" aria-hidden="true">#</a> 인사말</h3><ul><li>손위사람에게 : 고생하십시오X, 수고하십시오X</li></ul><h3 id="바르게-앉는-자세" tabindex="-1"><a class="header-anchor" href="#바르게-앉는-자세" aria-hidden="true">#</a> 바르게 앉는 자세</h3><ul><li>의자 왼편으로 들어가서 깊숙히 앉는다</li><li>허리와 가슴펴고 등받이에 기댄다, 다리를 꼬지 않고 무릎을 붙여 앉는다</li></ul><h3 id="지시-받을-때" tabindex="-1"><a class="header-anchor" href="#지시-받을-때" aria-hidden="true">#</a> 지시 받을 때</h3><ul><li>5W 2H (When Where Who What Why, How How much) 관점</li></ul><h2 id="_20대-자산형성" tabindex="-1"><a class="header-anchor" href="#_20대-자산형성" aria-hidden="true">#</a> 20대 자산형성</h2><h3 id="종잣돈-모으기" tabindex="-1"><a class="header-anchor" href="#종잣돈-모으기" aria-hidden="true">#</a> 종잣돈 모으기</h3><ul><li><p>종잣돈 : 본격적인 투자를 시작하는데 필요한 초기자금</p></li><li><p>가시적인 목표를 세우자 (ex 이번년에 500만원을 모으자)</p></li></ul><ol><li><p>금액 : 기존 저축금액을 <strong>제외한</strong> 나머지 금액</p></li><li><p>기간 : 1~2년의 단기계획, 안전한 적금이나 채권형 펀드</p></li><li><p>금융 상품 선택</p><ul><li><p>주택자금 : 주택청약저축, 노후자금 : 연금저축</p></li><li><p>결혼자금 : 혼합형 펀드, 비상금 : CMA상품, 종잣돈 : 적금/채권형 펀드</p></li></ul><ol><li><p>주택청약저축</p><ul><li><p>새로 지은 아파트를 분양 받으려는 사람들이 개설</p></li><li><p>1인 1계좌, 주택 소유 여부에 관계없이 가입 가능</p></li><li><p>공공분양 (주택공사) : 적금형태 (2~10)</p><ul><li>청약금액이 클수록 유리</li></ul></li><li><p>민간분양 (민간건설회사) : 가점제, 추첨제</p><ul><li>무주택 기간 길수록, 부양가족 많을수록 유리</li></ul></li></ul></li><li><p>국민 연금보험</p><ul><li>죽을때 까지 지급되는 연금상품, 소득이 있는 국민이라면 의무 가입</li><li>연급지급 시작 (61~65세)</li><li>보험료는 소득의 9%</li><li>직장인은 절반을 고용주가 부담</li><li>개인사업자나 주부는 전액을 자신이 부담</li></ul></li><li><p>퇴직 연금 제도</p><ul><li>퇴직금 : 근무기간 1년을 채울 때마다 한 달치의 월급을 모아놨다가 한번에 주는 돈</li><li>일시금/연금으로 나누어진다</li><li>퇴직금 제도 (회사가 파산 -&gt; 못 돌려받음)을 개선 하여 퇴직연금 제도 <ul><li>DB형 (회사가 운용지시) : 은행 예금 방식, 근로자가 미리 금액을 알 수 있다.</li><li>DC형 (근로자가 운용지시) : 주로 주식이나 채권 투자, 금액을 알 수 없다</li></ul></li></ul></li><li><p>주택 연금 제도 (역모기지론)</p><ul><li>만 55세 이상의 고령자가 금융기관에 자신이 살고 있는 주택을 담보로 제공한 뒤, 매달 고정적인 생활자금을 연금식으로 받는 장기주택저당대출</li></ul></li><li><p>금융정보 : 20대에 금융지식을 쌓는 것은 중요하다</p></li></ol></li></ol><h2 id="재무목표-설정-방법" tabindex="-1"><a class="header-anchor" href="#재무목표-설정-방법" aria-hidden="true">#</a> 재무목표 설정 방법</h2><h3 id="현명한-소비-생활" tabindex="-1"><a class="header-anchor" href="#현명한-소비-생활" aria-hidden="true">#</a> 현명한 소비 생활</h3><blockquote><p>건전한 가치관을 가지고, 예산 생활을 실천하는 생활</p></blockquote><h2 id="예산-생활" tabindex="-1"><a class="header-anchor" href="#예산-생활" aria-hidden="true">#</a> 예산 생활</h2><ul><li>희소성의 원칙 : 우리가 가진 재화는 무한하지 않다</li><li>소비가치관 : 소비의 우선순위에서 어떤 품목을 우선시할 것인가에 대한 각자의 신념</li><li>가처분 소득 = 총 소득 - 비소비지출 (세금, 국민연금, 건강보험)</li><li>예산 : 일정 기간 동안의 기대 수입에 기초한 <strong>지출 계획</strong></li><li>예산 생활의 장점 <ol><li>자신이 추구해야할 <u>재무욕구</u>를 구체적으로 알 수 있다.</li><li>자신의 수입금액, 지출금액 <u>재무상태</u>를 파악할 수 있다.</li><li>소득을 늘릴 방안이나 소비의 어떤 부분을 줄여야 하는지를 알 수 있다.</li><li>결과를 평가함으로써 문제점을 찾고 개선해 나갈 수 있다.</li></ol></li></ul><h3 id="재무-목표의-요건" tabindex="-1"><a class="header-anchor" href="#재무-목표의-요건" aria-hidden="true">#</a> 재무 목표의 요건</h3><ol><li>현실성 있는 목표 설정</li><li>구체적이면서 측정 가능한 목표 설정</li></ol><h2 id="블랙-컨슈머" tabindex="-1"><a class="header-anchor" href="#블랙-컨슈머" aria-hidden="true">#</a> 블랙 컨슈머</h2><h3 id="소비자-권익" tabindex="-1"><a class="header-anchor" href="#소비자-권익" aria-hidden="true">#</a> 소비자 권익</h3><ul><li>소비자보호법은 제정된 이래로 40년간 소비자 권익을 증진시킴</li></ul><h3 id="블랙컨슈머" tabindex="-1"><a class="header-anchor" href="#블랙컨슈머" aria-hidden="true">#</a> 블랙컨슈머</h3><blockquote><p>기업 등을 상대로 부당한 이익을 취하고자 제품을 구매한 후 고의적으로 악성 민원을 상습적으로 제기하는 자</p></blockquote><h3 id="비매너-컨슈머" tabindex="-1"><a class="header-anchor" href="#비매너-컨슈머" aria-hidden="true">#</a> 비매너 컨슈머</h3><ul><li><p>근로자에게 &#39;갑질&#39;하는 소비자</p></li><li><p>블랙컨슈머를 포함</p></li><li><p>=&gt; &#39;갑질&#39;의 피해를 당하는 근로자가 많아짐에 따라 &#39;<strong>산업안전보건정</strong>&#39; 개정</p></li></ul><h3 id="윤리적-소비" tabindex="-1"><a class="header-anchor" href="#윤리적-소비" aria-hidden="true">#</a> 윤리적 소비</h3><ul><li>소비자가 물건을 구입할때, 가격과 품질 뿐만 아니라 사회와 환경에 미치는 영향을 고려해야 한다는 것</li><li>친환경 소비</li><li>상거래 소비윤리 <ul><li>사업자와 거래할 때 정해진 계약의 의무를 지키는 일</li><li>바이콧 : 윤리적으로 바람직한 제품을 구입하는 구매운동</li></ul></li><li>로컬푸드 소비</li><li>공정무역 상품 구매</li><li>기부와 나눔</li></ul><h3 id="크라우드-펀딩" tabindex="-1"><a class="header-anchor" href="#크라우드-펀딩" aria-hidden="true">#</a> 크라우드 펀딩</h3><ul><li>온라인을 통해 다수의 소액투자자로부터 자금을 조달하는 것</li><li>종류 <ul><li>리워드형 : 제품을 생산자가 만들어내면 일반인이 선주문해서 구매하는 경우</li><li>기부형 : 공익적인 프로젝트를 진행하기 위해서 모으는 경우</li><li>대출형 : 개인들 사이에서 P2P 대출이 이루어지고, 리워드로 이자가 제공</li><li>투자형 : 투자자금을 지원하고 주식 및 채권 등의 보상을 받음</li></ul></li><li>펀딩 기한 내에 목표금액을 모으지 못하면 자금을 다시 되돌려 준다</li><li>발전 배경 <ul><li>중개 플랫폼등장, SNS활서오하, 대안적 기업금융, 크라우드펀딩법</li></ul></li></ul><h2 id="사회적-기업" tabindex="-1"><a class="header-anchor" href="#사회적-기업" aria-hidden="true">#</a> 사회적 기업</h2><h3 id="사회적-기업-1" tabindex="-1"><a class="header-anchor" href="#사회적-기업-1" aria-hidden="true">#</a> 사회적 기업</h3><ul><li><p>영리 기업과 비영리기업의 중간 형태</p></li><li><p><strong>사회적 목적</strong>을 우선적으로 추구하면서, 재화와 서비스의 생산판매 등 영업 행동을 수행하는 기업</p></li><li><p>공공성과 수익성</p><blockquote><p>(공공성) 자원봉사 &lt;- NGO &lt;- 사회적기업 -&gt; 협동조합 -&gt; 일반기업 (수익성)</p></blockquote></li><li><p>성공방안</p><ul><li>영리기업과 사회적 기업간의 파트너십을 모색할 것</li></ul></li><li><p>종류</p><ul><li>일자리제공형 : 취약계층에게 일자리를 30% 이상 제공하는 경우</li><li>사회서비스제공형 : 취약계층의 30% 이상에게 사회서비스를 수혜한 경우</li><li>혼합형 : 취약계층에게 일자리나 사회 서비스를 같이 제공하는 경우</li><li>지역사회공헌형 : 마을의 경제적, 사회적, 문화적 활성화에 공헌하는 경우</li><li>기타형 : 창의적, 혁신적 방식으로 사회문제를 해결하고자 노력한 경우</li></ul></li><li><p>소셜벤처 경연대회</p><ul><li>사회적 기업의 창업 아이디어를 발굴하고 사업화를 지원하는 대회</li></ul></li></ul><h2 id="커뮤니케이션-매너" tabindex="-1"><a class="header-anchor" href="#커뮤니케이션-매너" aria-hidden="true">#</a> 커뮤니케이션 매너</h2><h3 id="나-전달법-i-message" tabindex="-1"><a class="header-anchor" href="#나-전달법-i-message" aria-hidden="true">#</a> 나 전달법 (I-Message)</h3><blockquote><p>나를 주어로 자신의 감정이나 생각을 표현하는 전달법</p></blockquote><h3 id="대화의-협력성" tabindex="-1"><a class="header-anchor" href="#대화의-협력성" aria-hidden="true">#</a> 대화의 협력성</h3><ul><li>동양적 사고 : 침묵은 금이다, 일방통행형</li><li>서양적 사고 : 대화는 양방향, 리액션 중요</li></ul><h3 id="남성의-결혼-후-가족간의-호칭" tabindex="-1"><a class="header-anchor" href="#남성의-결혼-후-가족간의-호칭" aria-hidden="true">#</a> 남성의 결혼 후 가족간의 호칭</h3><table><thead><tr><th>구분</th><th>호칭 (직접)</th><th>지칭(3자)</th></tr></thead><tbody><tr><td>아내의 아버지</td><td>장인어른, 아버님</td><td>장인어른, OO 외할아버지</td></tr><tr><td>아내의 어머니</td><td>장모님, 어머님</td><td>장모님, OO 외할머니</td></tr><tr><td>아내</td><td>여보, OO엄마, 임자, 당신</td><td>집사람, 안사람, OO엄마, 아내</td></tr><tr><td>아내의 오빠</td><td>형님</td><td>좌동</td></tr><tr><td>아내의 남동생</td><td>처남, 자네</td><td>좌동</td></tr><tr><td>아내의 언니</td><td>처형</td><td>좌동</td></tr><tr><td>아내의 여동생</td><td>처제</td><td>좌동</td></tr><tr><td>딸의 남편</td><td>O서방</td><td>우리 사위</td></tr></tbody></table><h3 id="여성의-결혼-후-가족간의-호칭" tabindex="-1"><a class="header-anchor" href="#여성의-결혼-후-가족간의-호칭" aria-hidden="true">#</a> 여성의 결혼 후 가족간의 호칭</h3><table><thead><tr><th>구분</th><th>호칭 (직접)</th><th>지칭(3자)</th></tr></thead><tbody><tr><td>남편의 아버지</td><td>아버님</td><td>시아버지, OO 할아버지</td></tr><tr><td>남편의 어머니</td><td>어머님</td><td>시어머니, OO 할머니</td></tr><tr><td>남편</td><td>여보, OO아빠, 당신, 영감</td><td>남편, 아범, 그 이</td></tr><tr><td>남편의 형</td><td>아주버님</td><td>아주버님, 시숙</td></tr><tr><td>남편의 남동생</td><td>도련님(기혼), 서방님(기혼)</td><td>시동생</td></tr><tr><td>남편의 누나</td><td>형님</td><td>시누이</td></tr><tr><td>남편의 여동생</td><td>아가씨</td><td>시누이</td></tr><tr><td>아들의 아내</td><td>아가, 새아가</td><td>우리 며늘애</td></tr></tbody></table><h3 id="토론-시-경청자의-매너" tabindex="-1"><a class="header-anchor" href="#토론-시-경청자의-매너" aria-hidden="true">#</a> 토론 시 경청자의 매너</h3><ul><li>SMART 경청법 <ul><li>Subject (토론의 <strong>주제</strong> 생각)</li><li>Materials (인용하는 자료의 <strong>출처</strong> 유의)</li><li>Assertion (<strong>주장</strong>이 무엇인지 파악)</li><li>Reaction (<strong>반응</strong>[동의/반대]을 어떻게 할지 생각)</li><li>Trademark (다른 사람의 주장과 구별되는 <strong>특색</strong>이 무엇인지 찾아본다)</li></ul></li></ul><h3 id="통신-매너-편지" tabindex="-1"><a class="header-anchor" href="#통신-매너-편지" aria-hidden="true">#</a> 통신 매너 (편지)</h3><ul><li>안부 편지 : 격식을 차릴 필요 없음</li><li>감사 편지 : 행사 후 2~3일 이내 신속하게 보낼 것</li><li>초청편지 : 2주~4주 전에 육하원칙에 맞추어 보낼 것</li><li>위로편지 : 할까 말까 고민되면 쓰자, 진정성과 따뜻한 마음을 표현할 것</li></ul><h2 id="음주매너-1" tabindex="-1"><a class="header-anchor" href="#음주매너-1" aria-hidden="true">#</a> 음주매너(1)</h2><h3 id="적포도주" tabindex="-1"><a class="header-anchor" href="#적포도주" aria-hidden="true">#</a> 적포도주</h3><blockquote><p>12-13도 육류요리에 적합</p></blockquote><h3 id="백포도주" tabindex="-1"><a class="header-anchor" href="#백포도주" aria-hidden="true">#</a> 백포도주</h3><blockquote><p>10-13도, 생선요리에 적합</p></blockquote><h3 id="로제" tabindex="-1"><a class="header-anchor" href="#로제" aria-hidden="true">#</a> 로제</h3><blockquote><p>6-8도, 생선, 해물류, 닭고기와 어울림</p></blockquote><h3 id="아이스와인" tabindex="-1"><a class="header-anchor" href="#아이스와인" aria-hidden="true">#</a> 아이스와인</h3><blockquote><p>당도가 높고 과일 향이 진함</p></blockquote><h3 id="디저트-와인" tabindex="-1"><a class="header-anchor" href="#디저트-와인" aria-hidden="true">#</a> 디저트 와인</h3><blockquote><p>18도 전후</p></blockquote><h3 id="스파클링-와인" tabindex="-1"><a class="header-anchor" href="#스파클링-와인" aria-hidden="true">#</a> 스파클링 와인</h3><blockquote><p>10-14도, 파티용</p></blockquote><h3 id="포도주-라벨" tabindex="-1"><a class="header-anchor" href="#포도주-라벨" aria-hidden="true">#</a> 포도주 라벨</h3><ul><li>와인이름</li><li>생산지</li><li>와이너리(양조장) 이름</li><li>양조장 설립년도</li><li>당도</li></ul><h3 id="와인의-당도" tabindex="-1"><a class="header-anchor" href="#와인의-당도" aria-hidden="true">#</a> 와인의 당도</h3><ul><li>이탈리아 : (dry) &lt;- 세코 &lt;- 아보카토 &lt;-&gt; 아마빌레 -&gt; 돌체 -&gt; (sweet)</li><li>프랑스 : (dry) &lt;- 브리 &lt;- 세크 &lt;-&gt; 두스 -&gt; (므왈레, 리퀘르) -&gt; (sweet)</li></ul><h3 id="와인잔의-종류-사진자료" tabindex="-1"><a class="header-anchor" href="#와인잔의-종류-사진자료" aria-hidden="true">#</a> 와인잔의 종류 (사진자료)</h3><ul><li>보르도 (레드와인 잔), 부르고뉴(레드와인 잔)</li><li>화이트와인 잔, 스파클링 와인 잔 (샴페인 잔)</li></ul><h3 id="와인-따르는-방법" tabindex="-1"><a class="header-anchor" href="#와인-따르는-방법" aria-hidden="true">#</a> 와인 따르는 방법</h3><ul><li>라벨을 가리지 않게 잡는다</li><li>레드와인은 잔의 1/3 채움, 스파클리와인은 2/3 채움</li><li>와인의 양이 적어지면 와인의 온도가 올라가서 맛이 덜함, 상대방 의향을 물어보고 첨잔</li></ul><h3 id="와인-받는-방법" tabindex="-1"><a class="header-anchor" href="#와인-받는-방법" aria-hidden="true">#</a> 와인 받는 방법</h3><ul><li>잔을 테이블에 놓은 채 잔의 발 부분에 손을 댄 채 받는다</li><li>스월링 : 잔의 스템을 잡고 한쪽 방향으로 회전 -&gt; 향을 풍부하게</li></ul><h2 id="음주매너-2" tabindex="-1"><a class="header-anchor" href="#음주매너-2" aria-hidden="true">#</a> 음주매너(2)</h2><h3 id="파티에서-와인을-따르는-매너" tabindex="-1"><a class="header-anchor" href="#파티에서-와인을-따르는-매너" aria-hidden="true">#</a> 파티에서 와인을 따르는 매너</h3><ol><li>남성 호스트가 먼저 테이스팅 -&gt;</li><li>상석에 앉은 여성 -&gt; 시계방향 여성만 -&gt; 남성 -&gt; 주인 순으로 따름</li></ol><h3 id="남은-술-보관-방법" tabindex="-1"><a class="header-anchor" href="#남은-술-보관-방법" aria-hidden="true">#</a> 남은 술 보관 방법</h3><ul><li>일반적 : 서늘한 곳에 병을 뉘어서</li><li>전문적 : 와인병 안의 공기를 최소화</li></ul><h3 id="양주의-종류" tabindex="-1"><a class="header-anchor" href="#양주의-종류" aria-hidden="true">#</a> 양주의 종류</h3><ul><li>브랜디 : 증류한 포도주, 40~42도</li><li>위스키 : 곡주를 증류한 술</li><li>보드카 : 3無 (무색, 무취, 무미), 아주 차게하여 한번에 마심</li><li>데킬라 : 용설란의 즙에 설탕 넣고 발효, 40도~60도</li></ul><h3 id="중국-1" tabindex="-1"><a class="header-anchor" href="#중국-1" aria-hidden="true">#</a> 중국</h3><ul><li>손님을 초청한 경우 많이 마시도록 권함, 첨잔문화</li></ul><h3 id="일본" tabindex="-1"><a class="header-anchor" href="#일본" aria-hidden="true">#</a> 일본</h3><ul><li>이자카야, 자기 주량만큼 마심, 첨잔 방식, 한손으로 따르고 받음, 와리깡 (각자 부담)</li></ul><h3 id="프랑스" tabindex="-1"><a class="header-anchor" href="#프랑스" aria-hidden="true">#</a> 프랑스</h3><ul><li>식전 주를 작은 잔에 마심</li><li>백포도주, 앙트레 (전채요리) 제공</li><li>적포도주, 본식사 제공</li><li>술 종류마다 잔이 다름</li><li>식사 끝나면 커피, 코냑</li></ul><h3 id="독일" tabindex="-1"><a class="header-anchor" href="#독일" aria-hidden="true">#</a> 독일</h3><ul><li>음주가 생활의 일부, 다른 사람에게 술을 권하지 않음</li></ul><h3 id="영국" tabindex="-1"><a class="header-anchor" href="#영국" aria-hidden="true">#</a> 영국</h3><ul><li>스코틀랜드 위스키(스카치) 유명</li></ul><h3 id="미국" tabindex="-1"><a class="header-anchor" href="#미국" aria-hidden="true">#</a> 미국</h3><ul><li>옥외에서 음주 금지, 일요일에는 술을 팔지 않음</li></ul><h3 id="러시아" tabindex="-1"><a class="header-anchor" href="#러시아" aria-hidden="true">#</a> 러시아</h3><ul><li>보드카를 좋아함</li><li>복통-&gt;(보드카+소금), 감기-&gt;(보드카+후추), 컨디션저하(보드카+ 마늘 또는 양파)</li></ul><h2 id="흡연매너" tabindex="-1"><a class="header-anchor" href="#흡연매너" aria-hidden="true">#</a> 흡연매너</h2><h3 id="담배-속-유해물질" tabindex="-1"><a class="header-anchor" href="#담배-속-유해물질" aria-hidden="true">#</a> 담배 속 유해물질</h3><ul><li>일산화탄소 : 헤모글로빈과 결합하여, 산소공급을 저하시킴 -&gt; 산소쇼크</li><li>니코틴 : 도파민 분비 -&gt; 안정감, 긴장감 해소</li><li>타르 : 표피세포 등을 파괴하거나 염증을 일으킴</li></ul><h2 id="한국-음식" tabindex="-1"><a class="header-anchor" href="#한국-음식" aria-hidden="true">#</a> 한국 음식</h2><h3 id="특징" tabindex="-1"><a class="header-anchor" href="#특징" aria-hidden="true">#</a> 특징</h3><ul><li>주식과 부식의 구분 (주식 : 밥)</li><li>곡류의 다양한 조리법</li><li>발효 및 저장 식품의 발달 <ul><li>우리나라 5대 발효식품 : 장류, 김치, 식초류, 해류, 주류</li></ul></li><li>절식(명절음식)과 시식(계절음식)</li><li>자극적인 맛 : 부식에 고춧가루, 마늘, 생강 등 사용</li><li>공간전개형 상차림 : 식탁위에 한번에 전부 차려냄</li></ul><h3 id="김치의-발효-원리" tabindex="-1"><a class="header-anchor" href="#김치의-발효-원리" aria-hidden="true">#</a> 김치의 발효 원리</h3><ul><li>무, 배추의 효소가 당과 아미노산을 만듬</li><li>젖산균 (미생물의 한 종류) 생김</li><li>젖산과 소금이 만나 젖산을 생성</li><li>젖산균이 다른 미생물을 제거</li><li>김치는 젖산에 의해 발효됨</li></ul><h3 id="우리나라-지역별-김치-종류" tabindex="-1"><a class="header-anchor" href="#우리나라-지역별-김치-종류" aria-hidden="true">#</a> 우리나라 지역별 김치 종류</h3><table><thead><tr><th>지역</th><th>김치 종류</th><th>특징</th></tr></thead><tbody><tr><td>평안도</td><td>백김치, 가지김치</td><td>소고기 육수 사용</td></tr><tr><td>강원도</td><td>오징어김치, 더덕김치</td><td>채소와 해물 사용</td></tr><tr><td>서울, 경기도</td><td>보쌈김치, 오이소박이</td><td>새우젓, 조기젓 사용</td></tr><tr><td>충청도</td><td>나박김치, 호박김치</td><td>순하고 구수한 맛</td></tr><tr><td>경상도</td><td>부추김치, 깻잎 김치</td><td>진한 젓갈 맛, 자극적이고 매움</td></tr><tr><td>전라도</td><td>고들빼기, 나주동치미</td><td>다양한 젓갈과 해산물로 깊은 맛</td></tr><tr><td>제주도</td><td>전복김치</td><td>적은 양념으로 재료의 원래 맛을 살림</td></tr></tbody></table><h3 id="서양-음식과-비교" tabindex="-1"><a class="header-anchor" href="#서양-음식과-비교" aria-hidden="true">#</a> 서양 음식과 비교</h3><ul><li>한국 음식 : 습열조리 (steaming) 방식을 써서 숟가락 냄비 사용</li><li>서양 음식 : 건열조리 (roasting) 방식을 위해 팬과 오븐 사용, 포크 나이프 사용</li></ul><h2 id="글로벌전통음식-브라질-중국" tabindex="-1"><a class="header-anchor" href="#글로벌전통음식-브라질-중국" aria-hidden="true">#</a> 글로벌전통음식 : 브라질, 중국</h2><h3 id="한국-일본-중국의-젓가락-비교" tabindex="-1"><a class="header-anchor" href="#한국-일본-중국의-젓가락-비교" aria-hidden="true">#</a> 한국, 일본, 중국의 젓가락 비교</h3><table><thead><tr><th></th><th>한국</th><th>일본</th><th>중국</th></tr></thead><tbody><tr><td>명칭</td><td>젓가락</td><td>하시</td><td>콰이즈</td></tr><tr><td>재질</td><td>금속</td><td>나무</td><td>플라스틱, 대나무</td></tr><tr><td>형태</td><td>납작하고, 굵기 차이가 적다</td><td>끝이 뾰족하고 길이가 짧다, 굵기 차이가 크다</td><td>길이가 길다, 굵기 차이가 크다</td></tr></tbody></table><ul><li>중국의 식사 예절</li></ul><ol><li>큰 접시의 음식을 나누어 먹는다</li><li>좌석배치 : 주빈이 되는 손님이 상석(안쪽)에 앉고 주인은 하석 (바깥쪽)에 앉는다</li><li>주빈 우선 : 음식이 나오면 주빈 앞에 놓고, 술의 종류도 물어서 정한다</li><li>스푼은 탕을 먹을 때만 사용, 밥이나 탕이 담긴 그릇은 들고 먹는다. 사용한 숟가락은 뒤집어 놓는다</li><li>회적식탁 : 예의상 같이 돌려준다. 개인 접시나 컵을 놓지 않는다.</li></ol><h2 id="글로벌-전통음식-프랑스" tabindex="-1"><a class="header-anchor" href="#글로벌-전통음식-프랑스" aria-hidden="true">#</a> 글로벌 전통음식 : 프랑스</h2><h3 id="프랑스에서-금기시-되는-행동" tabindex="-1"><a class="header-anchor" href="#프랑스에서-금기시-되는-행동" aria-hidden="true">#</a> 프랑스에서 금기시 되는 행동</h3><ol><li>사람 앞에서 코를 훌쩍 거리는 행동</li><li>밖에서 다른 사람과 몸이 닿았을 때 사과를 하지 않는 행동</li><li>식당에서 직원을 부르는 행동 (눈이 마주치면 눈짓으로 호출)</li><li>외모 (바꿀 수 없는 것)에 대한 얘기 (친한 사이라도)</li><li>모르는 사람과 눈이 마주치는 것</li></ol><h3 id="프랑스-코스-요리" tabindex="-1"><a class="header-anchor" href="#프랑스-코스-요리" aria-hidden="true">#</a> 프랑스 코스 요리</h3><ul><li><p>순서</p><ol><li>아페리티프(식전주)</li><li>앙트레 / 오흐 되브흐</li><li>쁠라 (생선 요리 / 육류 요리)</li><li>프로마쥬 (치즈)</li><li>디저트</li><li>커피 / 차</li></ol></li><li><p>테이블 매너</p><ul><li>초대자에게 감사의 표시로 꽃이나 포도주, 화초 등의 가벼운 선물을 하고, 선물은 안주인에게 건낸다</li><li>미리 이야기 된 것이 아니면, 음식은 선물하지 말자, 과자나 케잌이 중복되지 않도록 한다</li><li>음식은 절대 남기지 않는다 (맛 없어서 남긴걸로 간주)</li><li>음식이 바뀔 때마다 &quot;맛있다&quot;는 칭찬을 빼놓아서는 안된다</li></ul></li><li><p>프랑스 와인 축제</p><ul><li>보르도 와인 페스티벌</li><li>보졸레 누보 축제</li></ul></li><li><p>프랑스 대표 음식</p><ol><li>바게트 : 밀가루 + 물 + 소금 + 이스트</li><li>크로와상 : 바게트 재료 + 설탕 + 버터 + 분유</li><li>에스까르고 : 식용 달팽이</li><li>푸아그라 : 지방이 많은 거위의 간</li><li>크레프 : 밀가루나 메밀가루 반죽을 얇게 부치고 그 위에 다양한 속재료를 얹어 싸먹는 방식</li></ol></li></ul>',248),r=[d];function t(n,u){return l(),a("div",null,r)}const s=i(h,[["render",t],["__file","MPLC.html.vue"]]);export{s as default};
